import React, { Component } from 'react';
import axios from 'axios';
import { confirmAlert } from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';

export default class ListTout extends Component {

    constructor(props) {
        super(props);
        this.state = { produit: [] };

    }
    componentDidMount() {
        axios.get('https://valimbavaka.herokuapp.com/api/users/newArticle')
            .then(response => {
                console.log('i am a response', response)
                this.setState({ produit: response.data });
                console.log('i am a produit', this.state.produit)
            })
            .catch(function (error) {
                console.log(error);
            })



    }

    liste() {
        return <div>
        <div class="row">

                    {
                        (this.state.profil.length > 0) ? (this.state.profil.map((obj) => {
                            return (
<div class="col-md-6 carde">
<div class="card">




<div class="card-body">


<h4 class="card-title" id='titrebe'
onClick={()=>{
  console.log(obj._id);
  localStorage.setItem('atelier',obj._id);

}}><center>{obj.titre}</center> </h4>
<img width="100%" height="300px" src={'https://valimbavaka.herokuapp.com/user/'+obj.image} alt="pdp" />
<p class="card-text">Description: {obj.description}</p>
<p class="card-text">Date: {obj.date}</p>
<p class="card-text">Horaire de debut: {obj.debut}</p>
<p class="card-text">Durée: {obj.dure}</p>
<p class="card-text">Nombre de place disponible: {obj.place_dispo}</p>
<p class="card-text">Nombre de place reserve: {obj.place_reserve}</p>
<p class="card-text" onClick={()=>{
console.log(obj.visibilite);

}}>Prix: {obj.prix}$</p>
<div className='row'>
  <div className='col-md-6'>
<Link to={'/profil/'+obj._id} className="btn btn-primary" id="metykosa">Edit</Link></div>


<div>
<div className='col-md-6'>

{obj.visibilite===true ?(<button onClick={(e)=>{
         e.preventDefault()
        axios.get("https://valimbavaka.herokuapp.com/masquer/"+obj._id).then(res=>{
             axios.get('http://valimbavaka.herokuapp.com/register/'+ localStorage.getItem('id')).then(res=>{
        console.log(res.data)
        this.setState({profil:res.data})
    })
            console.log(res.data)})

      //"/masquer/:_id"/register/:_id   
     }} className="btn btn-danger">Desactiver</button>):(<button onClick={(e)=>{
        e.preventDefault()
        console.log(obj._id)
       axios.get("https://valimbavaka.herokuapp.com/affichier/"+obj._id).then(res=>{
axios.get('https://valimbavaka.herokuapp.com/register/'+ localStorage.getItem('id')).then(res=>{
        console.log(res.data)
        this.setState({profil:res.data})
    })
            console.log(res.data)})

     }} className="btn btn-success">Activer</button>)}
  </div>
         </div>
         </div>




</div>
</div>
</div>)

                        })) : ('')
                    }

    </div>
    </div>
            }
    render() {
        return (
            <div>
                    {this.liste()}
                </div>
                );
            }
}
